package java_programs_01;

public class Arthemetic_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a= 15;
		int b= 40;
		
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);

	}

}
